import React, { useState } from 'react';
import Button from '@material-ui/core/Button';
import AddIcon from '@material-ui/icons/Add';
import List from './List';

const Todo = () => {
  
    const [itemsEvent, setitemsEvent] = useState('');
    const [storItem, setstorItem] = useState([]);
  
    const Ieve = (event) => {
        setitemsEvent(event.target.value);
    }
    
    const adding = () => {
        setstorItem((oldVal) => {
            return [...oldVal, itemsEvent]
        });
        setitemsEvent('');
    }
  
  
    return(
        <>
        <div>
            <div>
                <br/>
                <h1> TODO List</h1>
                <br/>
                <input type="text" placeholder="add items" onChange={Ieve} value={itemsEvent}/>
                <Button onClick={adding}><AddIcon/></Button>

                <ol>
                    {/* <li> {itemsEvent} </li> */}

                    {storItem.map((additem, index) => {
                        <List text={additem} key={index} id={index}/>;
                    })}
                </ol>
            </div>
        </div>
        </>
    );
};

export default Todo
