import React, { useState } from 'react';
import "./index.css";

const App = () => {

    const [num, setNum] = useState(0);

    const inc = () => {
        setNum(num + 1)
    }
    const dec = () => {
        setNum(() => {
            if(num > 0){
                setNum(num - 1);
            }else{
                alert('Error');
            }
        })
    }



return (
    <>
        <h1> {num} </h1>
        <button onClick={inc}> Inc </button>
        <button onClick={dec}> Dec</button>
    </>

);

}



export default App;
