import React, { useState } from 'react';
import TodoLists from './Todolist'



const App = () => {

  const [itemsEvent, setItemEvent] = useState("");
  const [items, setItems] = useState([]);

  const additem = (event) => {
    setItemEvent(event.target.value);
  }
  const Caditem = () => {
    setItems((oldItems) => {
      return [...oldItems, itemsEvent]
    })
    setItemEvent('');
  } 
  const deleteItems = (id) => {
    console.log('deleting')
    setItems((oldItems) => {
      return oldItems.filter((arr, index) => {
        return index !== id;
      });
    })
}


 return(
   <>
    <div>
      <div>
        <h1>Todo List</h1>
        <button onClick={Caditem}> Add </button>
        <br/>
        <hr/>
        <input type="text" placeholder="Add Your Work" onChange={additem} value={itemsEvent}/>
        <ol>
          {/* <li> {itemsEvent} </li> */}
          {
            items.map((curItems, index) => {
             return <TodoLists text={curItems} key={index} id={index} onSelect={deleteItems}/>
            })
          }
        </ol>
      </div>
    </div>
   </>
 );

}



export default App;
