import React, { useState } from 'react';
import AddIcon from '@material-ui/icons/Add';
import DeleteIcon from '@material-ui/icons/Delete';
import Button from '@material-ui/core/Button';
import "./index.css";

const App = () => {

    const [num, setNum] = useState(0);

    const inc = () => {
        setNum(num + 1)
    }
    const dec = () => {
        setNum(() => {
            if (num > 0) {
                setNum(num - 1);
            } else {
                setNum(num)
                alert('Error');
            }
        })
    }



    return (
    <>
            <h1> {num} </h1>
            
            <Tooltip title="Delete">
  <IconButton aria-label="delete">
    <DeleteIcon />
  </IconButton>
</Tooltip>
             <Button variant="contained" color="secondary" onClick={inc}> <AddIcon /></Button> 
  
                <Button variant="contained" color="primary" onClick={dec}> <DeleteIcon /> </Button>
    </>

);

}



export default App;
