import React, { useState } from 'react';



const App = () => {

    let Time = new Date().toLocaleTimeString();
    const [cTime, setTime] = useState(Time);

    const newTime = () => {
        Time = new Date().toLocaleTimeString();
        setTime(cTime);
    }
return(
    <>
    <h1>{Time}</h1>
    <button onClick={newTime}>Get Time</button>
    </>
);

};

export default App;
