import React, { useState } from 'react';
import reactDom from 'react-dom';
import "./index.css";

const App = () => {


    const blue = "blue";
    const [bg, setBg] = useState(yel);


    const changeBg = () => {
        const yel = "yellow";
        setBg(yel);


    }
  
  
    return(
        <>
        <div style={{ backgroundColor: blue }}>
        <button onClick={changeBg}>Click Me</button>
        </div>
    </>

    );

}



export default App;
