import React, { useState } from 'react';
import "./index.css";

const App = () => {


    const [fullInfo, setFullName] = useState({
        fname: "",
        lname: "",
        email: "",
        number: "",
    });

    const onSubmit = (event) => {
        event.preventDefault();
        alert('form submited');
    }

    const ineve = (event) => {
        console.log(event.target.value);
        console.log(event.target.name);

        const value = event.target.value;
        const name = event.target.name;

        setFullName((preValue) => {
            if (name === 'fname') {
                return {
                    fname: value,
                    lname: preValue.lname,
                    email: preValue.email,
                    number: preValue.number,
                };
            } else if (name === 'lname') {
                return {
                    fname: preValue.fname,
                    lname: value,
                    email: preValue.email,
                    number: preValue.number,
                };
            } else if (name === 'email') {
            return {
                fname: preValue.fname,
                lname: preValue.lname,
                email: value,
                number: preValue.number,
            };
        } else {
            return {
                fname: preValue.fname,
                lname: preValue.lname,
                email: preValue.email,
                number: value,
            };
        }
    })
}



return (
    <>
        <form onSubmit={onSubmit}>
            <div>
                <h1>Hellow {fullInfo.fname} {fullInfo.lname} </h1>
                <p> {fullInfo.email} </p>
                <p> {fullInfo.number} </p>
                <input
                    type='text'
                    placeholder="Enter Your Name"
                    name='fname'
                    onChange={ineve}
                    value={fullInfo.fname} />

                <input
                    type='text'
                    placeholder="Enter Your Last Name"
                    name='lname'
                    onChange={ineve}
                    value={fullInfo.lname} />

                <input
                    type='text'
                    placeholder="Enter Your Email"
                    name='email'
                    onChange={ineve}
                    value={fullInfo.email} />

                <input
                    type='text'
                    placeholder="Enter Your Number"
                    name='number'
                    onChange={ineve}
                    value={fullInfo.number} />

                <button type="submit">Submit</button>
            </div>
        </form>
    </>

);

}



export default App;
