import React, { useState } from 'react';
import TodoLists from './Todolist'



const App = () => {

  const [itemsEvent, setItemEvent] = useState("");
  const [items, setItems] = useState([]);

  const additem = (event) => {
    setItemEvent(event.target.value);
  }
  const Caditem = () => {
    setItems((oldItems) => {
      return [...oldItems, itemsEvent]
    })
    setItemEvent('');
  } 



 return(
   <>
    <div>
      <div>
        <h1>Todo List</h1>
        <button onClick={Caditem}> Add </button>
        <br/>
        <input type="text" placeholder="Add Your Work" onChange={additem} value={itemsEvent}/>
        <ol>
          {/* <li> {itemsEvent} </li> */}
          {
            items.map((curItems) => {
              <TodoLists/>
            })
          }
        </ol>
      </div>
    </div>
   </>
 );

}



export default App;
