import React, { useState } from 'react';



const App = () => {

  const [itemsEvent, setItemEvent] = useState("");
  const [items, setItems] = useState([]);

  const aditem = () => {
    setItems((oldItems) => {
      return [...oldItems, itemsEvent]
    })
  } 

  const additem = (event) => {
    setItemEvent(event.target.value);
  }

 return(
   <>
    <div>
      <div>
        <h1>Todo List</h1>
        <button onClick={aditem}> Add </button>
        <br/>
        <input type="text" placeholder="Add Your Work" onChange={additem}/>
        <ol>
          <li> {itemsEvent} </li>
          {
            items.map((curItems) => {
              return <li> {curItems} </li>
            })
          }
        </ol>
      </div>
    </div>
   </>
 );

}



export default App;
