import React, { useState } from 'react';
import Button from '@material-ui/core/Button';
import AddIcon from '@material-ui/icons/Add';


const Creatnote = (props) => {

    const addnotes = () => {
        props.prevData(note);
        setnote(({
            titel: '',
            contant: '',
        }))
    }

    const [note, setnote] = useState({
        titel: '',
        contant: '',
    })
 
    const inputEvent = (event) => {
        const {name, value} = event.target;

        setnote((preVal) => {
            return{
                ...preVal,
                [name] : value,

            }
        })
    }

    const [expand, setExpand] = useState(false)

    const expandIt = () => {
      setExpand(true);
    }
    const hideIt = () => {
        setExpand(false);
      }
      
 return(
   <>
    <Button  onClick={hideIt} ><AddIcon/> hide </Button>
    <div>
      {expand ?  <input type="text" value={note.titel} name="titel" onChange={inputEvent} placeholder="Titel"/> : null} 
        <textarea cols="" rows="" value={note.contant} onClick={expandIt} name="contant" onChange={inputEvent} placeholder="write a note" autoComplete="off"></textarea>
        <Button onClick={addnotes}><AddIcon/></Button>
    </div>
   </>
 );

}

export default Creatnote;
