import Button from '@material-ui/core/Button';
import AddIcon from '@material-ui/icons/Add';
import Card from '@material-ui/core/Card';

const Creatnote = (props) => {

    const addnotes = () => {
        props.prevData(note);
    }

    const [note, setnote] = useState({
        titel: '',
        contant: '',
    })

    const inputEvent = (event) => {
        const {name, value} = event.target;

        setnote((preVal) => {
            return{
                ...preVal,
                [name] : value,

            }
        })
    }
    const input_info = (props) => {
        return(
            <>
            <input type="text" value={note.titel} name="titel" onChange={inputEvent} placeholder="Titel"/>
            </>
        )
    }


 return(
   <>
    <div>
        <input type="text" value={note.titel} name="titel" onChange={inputEvent} placeholder="Titel"/>
        <textarea cols="" rows="" value={note.contant} name="contant" onChange={inputEvent} placeholder="write a note" autoComplete="off"></textarea>
        <Button onClick={addnotes}><AddIcon/></Button>
    </div>
    <Card info={input}/>
   </>
 );

}

export default Creatnote;
